                        BreedsDescr = "\t\t\tЯпонский бобтейл создаёт впечатление кошки средней величины, крепкого сложения и мускулистой, но не округлой, скорее стройной, чем массивной. Уникальный постав глаз в сочетании с высокими скулами и длинным, равномерно широким носом, явно отличают голову японского бобтейла от других пород восточного происхождения (особенно в профиль). Короткий хвост наподобие заячьего обладает опушением, придающим ему вид помпона и скрывающим особенности костного строения.",
                        BreedsImage = "Japanese_bobtail5.jpg",
                        BreedsImage2 = "JB29.jpg",
                        BreedsWeight = "от 3 до 5 кг",
                        BreedsCost = "25-55 тыс. руб",
                        BreedsPersonality = "\t\t\tОбщительный, любопытный, компанейский.",
                        BreedsHistory = "\t\t\tИстория существования японских бобтейлов насчитывает более тысячи лет. По одной из версий, в Японию коты попали на кораблях, шедших из Китая, приблизительно в IX веке н.э. По другой версии, кошки появились в Стране Восходящего Солнца гораздо раньше.",
                        BreedsCatCare = "\t\t\tБобтейлов всегда одолевает жажда приключений, любопытство, желание разведать «а что же хозяин хранит на верхней полке». Причем самый разгар активности приходится на молодой возраст, когда ежедневно можно удивляться тому, что в очередной раз исполнил изобретательный кот.",
                        BreedsTraining = "\t\t\tЯпонские кошки наделены невероятно активным темпераментом и не могут долго сидеть без движения. Особенно они любят заниматься аппортировкой и ловлей воображаемых мух. Чтобы подвижные и прыгучие японцы могли выплескивать накопленную энергию, им обустраивают спортивный уголок с лесенками и многоуровневыми площадками."
                    });
                    db.SaveChanges();
            }
        }

        public static void FillBD_Behaviors() // заполнение БД поведение
        {
            string dbPath = DependencyService.Get<IPath>().GetDatabasePath(DBFILENAME);
            using (var db = new ApplicationContext(dbPath))
            if (db.CatBehaviors.Count() == 0) // поведение котов
            {
                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Длинное мяяяу",
                        BehaviorDescr = "\t\t\tНадо рассматривать по ситуации — это может быть приветствие человека после долгого отсутствия, просьба голодного кота или жалоба на другого питомца или члена семьи.",
                        BehaviorImage = "CatScream.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Короткое мя",
                        BehaviorDescr = "\t\t\tГоворит, что питомец чем-то озадачен и задает вопрос.",
                        BehaviorImage = "KitenMay2.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Короткое мяу",
                        BehaviorDescr = "\t\t\tМожет означать, что угодно, прислушайтесь к интонации, если кошка мяукает спокойно, то, возможно, она куда-то зовёт или хочет, чтобы вы подвинулись и уступили ей местечко возле себя, а громкое мяу может указывать на требование или недовольство.",
                        BehaviorImage = "itenMay.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Мурчание",
                        BehaviorDescr = "\t\t\tПоказывает обожание, довольство, успокаивает вас или себя, лечит боль.",
                        BehaviorImage = "Cat3.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Беззвучное мяу",
                        BehaviorDescr = "\t\t\tКогда кошка открывает пасть, а звук не слышен; кошка, на самом деле, мяучит, но человеческое ухо не способно уловить это звуковой диапазон, это значит, что питомец обеспокоен и зовёт за собой.",
                        BehaviorImage = "KitenMay3.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Пронзительный крик",
                        BehaviorDescr = "\t\t\tКрик боли или предвестник драки.",
                        BehaviorImage = "Cat9.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Шипение",
                        BehaviorDescr = "\t\t\tПредупреждение не подходи!",
                        BehaviorImage = "Cat8.jpg"
                    });

                    db.CatBehaviors.Add(new CatBehavior
                    {
                        BehaviorName = "Движения кошки",
                        BehaviorDescr = "\t\t\tCмотрит пристально и медленно закрывает глаза — целует, это любовь; развела уши — насторожилась; хлещет хвостом по бокам — недовольна; выгнула спину, придала уши, распушила хвост — готовится напасть; бодает головой, трется о ноги — метит вас для защиты, как собственность; хвост трубой, а кончик загнут — рада видеть; усы и хвост повисли — грусть-тоска; приподняла переднюю лапку — задумалась.",
                        BehaviorImage = "Kiten.jpg"
                    });

                    db.SaveChanges();
            }
        }

        public static void FillBD_Notes() // заполнение БД заметки
        {
            string dbPath = DependencyService.Get<IPath>().GetDatabasePath(DBFILENAME);
            using (var db = new ApplicationContext(dbPath))
                if (db.CatNotes.Count() == 0)
                {
                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Как приучить кошку к новому дому",
                        NoteDescr = "\t\t\tЕсли вы не представляете жизнь без домашнего животного и решили завести себе питомца, то ветеринары советуют брать животное в возрасте 10-12 недель от роду. В этом возрасте питомец начинает привыкать к человеку. \r\n\r\n\t\t\tКогда вы принесете пушистого любимца в дом, сумку или корзину, в которой принесли животное не нужно сразу убирать. Поставьте ее в уголок, пока кошка не пройдет адаптацию к новому месту, это будет для нее убежищем, кошка не раз воспользуется им. На видном для питомца месте поставьте необходимые сосуды — миску для еды и воды, лоток, куда кошка будет ходить в туалет.\r\n\r\n\t\t\tС появлением нового дома, кошка может вести себя очень насторожено, забиться в угол или наоборот все обнюхивать. В такой момент животное испытывает большой стресс, человек должен помочь пережить питомцу этот этап. Человек в такой момент должен сам быть спокоен, не кричать, не вести себя агрессивно пока питомец привыкает к новому месту.\r\n\r\n\t\t\tПостарайтесь несколько дней как можно больше проводить время с новым членом семьи, уделяйте ему больше внимания и тогда период адаптации закончится намного быстрее.",
                        NoteImage = "Cat6.jpg"
                    });

                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Чем нельзя кормить кошку",
                        NoteDescr = "\t\t\tНе всем подряд можно кормить кошку, многие продукты несут большую опасность. Давайте разберем перечень этих продуктов.\r\n\r\n\t\t\tРыба несет вред для кошек, она содержит очень много белка, который увеличивает нагрузку на почки животного, тем самым разрушает минеральный баланс, после чего начинается мочекаменная болезнь.\r\n\r\n\t\t\tНельзя давать кошкам мучные изделия и бобовые культуры, они вызывают метеоризм у животного и расстройство кишечника.\r\n\r\n\t\t\tНельзя кормить кошек свининой, она очень жирная для них. Жирная еда вызывает ожирение внутренних органов у кошки, а так же в свином мясе могут быть паразиты.\r\n\r\n\t\t\tТак же нельзя, чтобы еда, которую вы даете кошке была с приправами.\r\n\r\n\t\t\tСледите, чтобы кошки не добрались до комнатных растений, так как они, могут быть ядовиты.",
                        NoteImage = "Cat4.jpg"
                    });

                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Как приучить кошку к когтеточке",
                        NoteDescr = "\t\t\tЕсли кошка начала портить мебель, а стричь когти уже поздно, то нужно в срочном порядкеприобрести вашему питомцу когтеточку. Для начала нужно понаблюдать какое место стал портить питомец.\r\n\r\n\t\t\tЕсли это косяк или стена с обоями, то когтеточку лучше купить с уголком из ковролина. Установите ее на то место, которое стала драть кошка. По началу такой инвентарь кошка может принять агрессивно, но есть несколько хитростей, которые помогут вам приучить питомца к когтеточке.\r\n\r\n\t\t\tВо-первых, нужно взять питомца на руки, поднести к когтеточке и поставить лапку на нее, затем слегка нажать на лапку, чтобы кошка выпустила когти, и провести так лапкой сверху в низ. Такой трюк нужно проделать несколько раз, можно не подряд, а в течение дня или нескольких дней.\r\n\r\n\t\t\tВо-вторых, сейчас продают специальные спреи для такого метода, в основном они на основе мяты.Нужно только побрызгать когтеточку. Ветеринары советуют не использовать валерьянку, он является наркотиком для котов и кошек.",
                        NoteImage = "Cat5.jpg"
                    });

                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Уход за шерстью",
                        NoteDescr = "\t\t\tОчень большую роль для здоровья кошек и котов играет уход за их шерстью. Эти животные очень чистоплотны, они вылизывают свою шерсть очень тщательно. Бывает таких усилий мало и поэтому ветеринары настоятельно рекомендуют помогать кошкам в этом деле.\r\n\r\n\t\t\tУхаживать за шерстью животного вам помогут гребни и щетки, которые можно приобрести в зоомагазине. Старайтесь как можно чаще осматривать кошек, чтобы сразу выявить у них какое-либо заболевание или обнаружить паразитов. Вычесывание кошек облегчит жизнь не только им, но и вам.\r\n\r\n\t\t\tШерсть, которую вы вычешите, могла бы оказаться на диване или на полу по всей квартире. Когда кошка сама себя вылизывает, она съедает свою шерсть, чтобы избавиться от этого комка, кошка начинает кашлять, вызывая этим рвоту. Это нормальное явление.\r\n\r\n\t\t\tЕсли у кошки шерсть короткая, то нужно вычесывать ее два раза в неделю, а если шерсть длинная, то один раз в день, чтобы шерсть не закаталась, иначе придется удалять этот комок только ножницами. Старайтесь вычесывать кошек аккуратно, чтобы не повредить кожу.",
                        NoteImage = "Cat2.jpg"
                    });

                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Купание кошек",
                        NoteDescr = "\t\t\tКошки – невероятно чистоплотны, они могут по несколько часов в день тратить на вылизывание своей шубки. Однако бывают ситуации, когда одного вылизывания недостаточно и мытье кошки – неизбежная процедура. Например животное сильно испачкалась, появились блохи или подготовка к выставке. \r\n\r\n\t\t\tПеред тем, как вымыть кошку, ее необходимо тщательно вычесать и подстричь когти специальными ножницами. Мыть животное рекомендуется в небольшой и неглубокой емкости, на дно которой положить полотенце, чтобы питомец не скользил и не волновался. Вода должна быть теплой. Кошку нужно погружать медленно, придерживая голову, чтобы вода не попала в нос и уши. Специальный шампунь следует наносить аккуратно на всю шерсть, не задевая мордочку. Затем осторожно смыть его до характерного «скрипа шерсти». После купания вода должна слегка стечь. Выньте кошку из ванны, заверните в полотенце и некоторое время держите на руках.\r\n\r\n\t\t\tЧто делать, если любимица категорически не приемлет воды и каждое купание заканчивается бешенством и истерикой? Современные производители предлагают две альтернативы: Шампунь в виде спрея и сухой шампунь. Они могут сильно помочь с поддержанием гигиены кошки.",
                        NoteImage = "WaterCat.jpg"
                    });
                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Поиграй со мной",
                        NoteDescr = "\t\t\tЧтобы оставаться сильными, ловкими стройными, коты обязательно должны двигаться. Кроме того, игры помогают животным усваивать навыки выживания, учат защищаться и развивают охотничье мастерство. \r\n\r\n\t\t\tОбычно коты развлекают себя сами, что выглядит весьна забавна. Но в то же время хозяева должны обеспечить их игрушками и ежедневно хотя бы понемногу играть со своими любимцами. Иначе деятельный котенок, скорее всего, станет бегать по штором, о взрослый кот обленится и растолстеет. Особенно важно найти способ активного времяпрепровождения для питомцев, которые не выходят на улицу. \r\n\r\n\t\t\tКстати, играя со своим котом, вы сможата установить с ним поистине фантастические отношения и стать лучшими друзьями на всю жизнь!",
                        NoteImage = "PlayGame.jpg"
                    });
                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Как приучить к лотку",
                        NoteDescr = "\t\t\tДля первого знакомства котенка с туалетным местом следует посадить его в лоток и дать время все там изучить. Главное для котенка – понять, что это место «то самое». Не стоит удерживать его насильно, но если он захотел сходить в туалет то стоит подождать. Сразу же чистить лоток не нужно. По запаху малыш в следующий раз сможет быстро найти свой горшок.\r\n\r\n\t\t\tКак понять, что котенок хочет в туалет?\r\n\t\t\tВ раннем возрасте  перед походом в туалет они начинают поиски – принюхиваются, осматривают углы, ищут место. Почти все малыши крутятся вокруг себя. Котята бегут в лоток после еды и после сна. Зная эту «хитрость», можно сразу посадить малыша в туалет, не дожидаясь сигналов.\r\n\r\n\t\t\tКотенок может с первого раза не запомнить нужное место. Поэтому заметив что он начинает готовится «сделать свои дела», следует отнести его в лоток и проследить за ним до «завершения мисси». Закрепить результат можно похвалой любимца, погладить и поощрить ласковым голосом. \r\n\r\n\t\t\tЕсли котенок сходил в неположенном месте, в недовольном тоне скажите что так делать нельзя и отнесите его к лотку – покажите, куда ему нужно ходить. \r\n\r\n\t\t\tОтсутствие положительного результата  к месяцу может говорить, что любимцу что-то не нравиться (место, наполнитель, резкие запахи рядом или от лотка). Если проблема так и не решилась, отвезите котенка к ветеринару.",
                        NoteImage = "Lotok.jpg"
                    });
                    db.CatNotes.Add(new CatNote
                    {
                        NoteName = "Когда нужно вводить кошкам первые вакцины",
                        NoteDescr = "\t\t\tВпервые котята подвергаются вакцинации в первые 8-12 недель своей жизни, раньше делать это не имеет смысла, так как в организме у котят уже есть антитела, полученные вместе с молоком матери. С течением времени иммунитет начинает слабеть, вот тогда и рекомендуется начинать делать кошке прививку впервые.\r\n\r\n\t\t\tПосле того, как котятам будет введена первая вакцина от панлейкопении, калицивироза и ринотрахеита, через месяц рекомендуется сделать ревакцинацию, совмещая с вакциной от бешенства. В дальнейшей жизни котенка делать ревакцинацию рекомендуется раз в год.",
                        NoteImage = "Privivka.jpg"
                    });
                    db.SaveChanges();
                }   
        }


